current.custom_css:
.m-topbar a[href*="pinterest"],
.m-topbar a[href*="twitter"] {
	display: none;
}

body .m-product-tag--new {
  background-color: #64BF99;
}
body .m-product-tag--sale {
  background-color: #DA3F3F;
}
.m-icon-box--small {
  border-top: 1px solid rgb(var(--color-border));
}
.m-icon-box--small .m-icon-box__heading {
  line-height: 26px;
  font-size: 18px;
  margin-bottom: 6px;
}
body .m-footer--block .social-media-links {
  gap: 12px;
  margin: 0;
  padding: 5px 0;
  flex-wrap: wrap;
}

body .m-footer--block .social-media-links a {
  color: #222;
  width: 48px;
  height: 48px;
  padding: 0;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  border-radius: 50%;
  background-color: #E9E9E9;
  transition: all .3s;
}

body .m-footer--block .social-media-links a:hover {
  background-color: #222;
  color: #fff;
  box-shadow: 0 0 0 0.2rem #222;
}

@media (min-width: 1536px) {
  body .m-footer--block-newsletter .m-footer--block-title {
    font-size: 42px;
  }
}
